import styled from "styled-components";
import {
  MdOutlineCheckBox,
  MdOutlineCheckBoxOutlineBlank,
} from "react-icons/md";

const TodoItemLi = styled.li`
  display: flex;
`;

const CheckBoxDiv = styled.div`
  width: 30px;
  height: 30px;
  background-color: #eee;
  border: 1px solid #000;
  border-radius: 50%;
`;

const Text = styled.p`
  font-size: 18px;
`;

export default function TodoItem({ todos, onToggle }) {
  const { id, text, checked } = todos;
  return (
    <TodoItemLi>
      <CheckBoxDiv
        onClick={onToggle(id)}
        checked={
          checked ? <MdOutlineCheckBox /> : <MdOutlineCheckBoxOutlineBlank />
        }
      />
      <Text>{text}</Text>
      <div>수정</div>
      <div>삭제</div>
    </TodoItemLi>
  );
}
