import React, { useState } from "react";
import styled from "styled-components";
import { AiOutlinePlus } from "react-icons/ai";

const CreateBtn = styled.button`
  width: 50px;
  height: 50px;
  font-size: 20px;
  color: #fff;
  border: none;
  border-radius: 50%;
  background-color: #83d483;
  &:hover {
    background-color: #96e896;
  }
  &:active {
    background-color: tomato;
    transform: rotate(45deg);
  }
`;

const Createcontainer = styled.section`
  display: flex;
  padding: 10px 0;
  justify-content: space-between;
`;

const CreateForm = styled.form`
  width: 100%;
  margin-right: 10px;
`;

const CreateInput = styled.input`
  width: 100%;
  height: 50px;
  border-radius: 7px;
`;

export default function CreateTodo({ onCreate }) {
  const [todoValue, setTodoValue] = useState(""); // todo State
  // const [open, setOpen] = useState(false); 버튼 클릭 시 input 창을 열지 안열지

  function handleChange(e) {
    console.log(e.target.value);
    setTodoValue(e.target.value);
  }
  function handleSubmit(e) {
    onCreate(todoValue);
    e.preventDefault();
    console.log(`li로 출력될 todo 저장 완료✅ ${todoValue}`);
    setTodoValue("");
  }

  return (
    <Createcontainer>
      <CreateForm onSubmit={handleSubmit}>
        <CreateInput
          type="text"
          placeholder="할 일을 입력 후, Enter를 눌러주세요!"
          onChange={handleChange}
          value={todoValue}
        />
        <CreateBtn type="submit">
          <AiOutlinePlus />
        </CreateBtn>
      </CreateForm>
    </Createcontainer>
  );
}
