import styled from "styled-components";

const TodoTitSection = styled.section`
  padding-bottom: 20px;
  border-bottom: 1px solid black;

  h2 {
    font-size: 32px;
  }

  p {
    font-size: 20px;
    color: #9e9c9c;
    margin-top: 10px;
  }

  .TodoCountCont {
    display: flex;
    gap: 15px;
    margin-top: 10px;
  }

  .ing {
    color: tomato;
  }

  .finish {
    color: green;
  }
`;

export default function TodoTitle() {
  const dt = new Date();
  const year = dt.getFullYear();
  const month = dt.getMonth() + 1;
  const date = dt.getDate();
  const dayArr = ["Ïùº", "Ïõî", "Ìôî", "Ïàò", "Î™©", "Í∏à", "ÌÜ†"];
  const day = dayArr[dt.getDay()]; // ÏöîÏùº

  let [todoCount, setTodoCount] = useState(0);
  let [todoFinishCount, setTodoFinishCount] = useState(0);
  return (
    <TodoTitSection>
      <h2>
        {year}ÎÖÑ {month}Ïõî {date}Ïùº
      </h2>
      <p>{day}ÏöîÏùº</p>
      <div className="TodoCountCont">
        <span className="ing">üìç {todoCount}Í∞ú ÎÇ®Ïùå</span>
        <span className="finish">‚úÖ {todoFinishCount}Í∞ú ÏôÑÎ£å</span>
      </div>
    </TodoTitSection>
  );
}
